#{item}
) GROUP BY template_id UNION ALL SELECT template_id, NULL `name`, NULL type, 0 itemCount, COUNT( 1 ) graphCount, 0 classifyCount, NULL templateLable, 0 triggerCount FROM graph WHERE template_id IN (
#{item}
) GROUP BY template_id UNION ALL SELECT template_id, NULL `name`, NULL type, 0 itemCount, 0 graphCount, COUNT( 1 ) classifyCount, NULL templateLable, 0 triggerCount FROM cloud_classify_template WHERE template_id IN (
#{item}
) GROUP BY template_id UNION ALL SELECT template_id, NULL `name`, NULL type, 0 itemCount, 0 graphCount, 0 classifyCount, GROUP_CONCAT( `name` ) templateLable, 0 triggerCount FROM template_lable WHERE template_id IN (
#{item}
) GROUP BY template_id UNION ALL SELECT id AS template_id, `name` AS `name`, type, 0 itemCount, 0 graphCount, 0 classifyCount, NULL templateLable, 0 triggerCount FROM template WHERE id IN (
#{item}
